Evidence<script type='text/javascript'>$(document).ready(
	function () {$('.btn').each(function (index) {
	var selector = $(this).attr('data-target');
	path = $(selector).find('.card-body').text();
	if (path.length > 15) {
		pathStart = path.indexOf(':\\\\');
		pathStart = pathStart - 1;
		pathEnd = path.indexOf('.png');
		pathEnd = pathEnd + 4;
		path = path.substring(pathStart, pathEnd);
		$(selector).find('.card-body').html('<img src="' + path + '" width="100%"></img>');
		}
	});
});
</script>

